import './x.css'
import Navbar from './Navbar'
import { useState,useEffect} from 'react';
import Axios  from 'axios';
import Modal from 'react-modal'
function Account()
{
    var name=JSON.parse(localStorage.getItem('name'));
    var Email=JSON.parse(localStorage.getItem('Email'));
    const [pic,setpic]=useState("");
    const [password,setpassword]=useState("");
    const [modalISOpen,setModalISOpen]=useState(false)
    useEffect(() => {
        setpic("https://tse4.mm.bing.net/th?id=OIP.TUZxSRMq6m2pqs9UId34LAHaEo&pid=Api&P=0&w=255&h=160")
    }, [])
    const changehandler=(event)=>{
        event.preventDefault();
        console.log(password)
        Axios.post('http://localhost:3001/update',{
               Name:name,
               Password:password
        }).then((response)=>{
            console.log("Updates successfully")
        }).catch((error)=>{
            console.log("Error")
        })
    }
    const passhandler=(event)=>{
        setpassword(event.target.value)
    }
    const Imagehandler=(event)=>{
        console.log(event.target.value)
    }
    return(
        <>
        <Navbar/>
            <img src={pic}  name="avatar" accept=".png, .jpg, .jpeg" alt=""></img>
            <form class= "Block">
	        <img src="dp2.jpg" alt="Avatar"></img>
			<br></br>
			<label>Username</label>
			<br></br>
			<br></br>
			<label>Email</label>	
			<br></br>
			<br></br><br></br><br></br>
			<button  class="btn-dark">My Orders</button>
            <button  class="btn-dark">Change password</button>
			<br></br>
	       </form>
     <button type='submit' onClick={changehandler}>Change password</button>
    <input type='file' style={{borderRadius:"20px",backgroundColor:"grey",size:"10px"}} onChange={Imagehandler}></input> 
    <button className='bt' type="submit" style={{borderRadius:'20px'}} onClick={Imagehandler}>Submit</button>
    <Modal class="modal" isOpen={modalISOpen} style={{
    overlay: {
      position: 'fixed',
      left: '260px',
      backgroundColor: 'transparent',
      width:"700px",
      outline:"no"
    },
    content: {
      position: 'absolute',

    }
  }}>
  <img className="image2" src={pic} alt=""></img>
  <b><p>Description</p></b>
  <p>one</p>
  <p>two</p>
  <p></p>
  <button className='closing' onClick={()=>setModalISOpen(false)}>Close</button>
  </Modal>
    </>
    )
}

export default Account
