import { useState } from "react";
import { ethers } from "ethers";
// Import ABI Code to interact with smart contract
import Greeter from "../artifacts/contracts/Greetings.sol/Greetings.json";

// The contract address
const greeterAddress = "0x9fE46736679d2D9a65F0992F2272dE9f3c7fa6e0";
export async function requestAccount() {
    await window.ethereum.request({ method: "eth_requestAccounts" });
  }

  // Fetches the current value store in greeting
export async function fetchGreeting() {
    // If MetaMask exists
    if (typeof window.ethereum !== "undefined") {
        console.log("hello")
        await requestAccount();
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      console.log(signer)
      const contract = new ethers.Contract(
        greeterAddress,
        Greeter.abi,
        signer
      );
      try {
        const data = await contract.getGreeting();
      console.log(data)
      } catch (error) {
        console.log("Error: ", error);
      }
    }
  }

