<h2 style={{marginTop:"20px",marginLeft:"500px"}}>Search Rooms</h2>
<b><hr size="5" width="100px" style={{marginTop:"20px",marginLeft:"550px",color:"#654321"}}></hr></b>
<div class="row">
  <div class="col" style={{marginLeft:"300px"}}>
  <label class="form-check-label">
  <label for="sel1">Room types:</label>
  <select class="form-control" id="sel1" onChange={typeHandler}>
      <option > </option>
    <option>Economic</option>
    <option >Buisness</option>
    <option >premium</option>
  </select>
  </label>
  </div>
  <div class="col">
  <p>Price:${state}</p>
  <input type="range" class="form-range" min="0" max="600"  onChange={inputHandler} style={{width:"100px",marginTop:"-10px",marginleft:"600px"}}></input>
</div>
</div>

<Card />

</>
