import './x.css'
import Navbar from './Navbar'
import { useState,useEffect} from 'react';
import Axios  from 'axios';
function Account()
{
    var name=JSON.parse(localStorage.getItem('name'));
    var Email=JSON.parse(localStorage.getItem('Email'));
    const [pic,setpic]=useState("");
    const [password,setpassword]=useState("");
    useEffect(() => {
        setpic("https://tse4.mm.bing.net/th?id=OIP.TUZxSRMq6m2pqs9UId34LAHaEo&pid=Api&P=0&w=255&h=160")
    }, [])
    const changehandler=(event)=>{
        event.preventDefault();
        console.log(password)
        Axios.post('http://localhost:3001/update',{
               Name:name,
               Password:password
        }).then((response)=>{
            console.log("Updates successfully")
        }).catch((error)=>{
            console.log("Error")
        })
    }
    const passhandler=(event)=>{
        setpassword(event.target.value)
    }
    const Imagehandler=(event)=>{
        console.log(event.target.value)
    }
    return(
        <>
    <form className='form'>
		<center><img src="dp2.jpg" alt="Avatar"></img></center>
			<br></br>
			<label>Username</label>
			<br></br>
			<input class="input" type="text" name="username" id="user"></input>
			<br></br><br></br>
			<label>Email</label>	
			<br></br>	
			<input class="input" type="email" name="email" id="email"></input>
			<br></br><br></br><br></br>
			<button  class="btn-dark">My Orders</button>
            <button  class="btn-dark">Change password</button>
			<br></br>
	</form>
    <input type='file' style={{borderRadius:"20px",backgroundColor:"purple"}} onChange={Imagehandler}></input> 
    <button type="submit" onClick={Imagehandler}>Submit</button>
    </>
    )
}

export default Account
