import './Card.css'
function Card(props)
{
    return(
      <div class="card" style={{width:"18rem"}}>
  <img class="card-img-top" src={props.source} alt=""></img>
  <div class="card-body">
    <h5 class="card-title" style={{color:"black"}}>{props.price}</h5>
    <p class="card-text">{props.type}</p>
  </div>
  </div>
    )
}

export default Card
