import './x.css'
import Navbar from './Navbar'
import { useState } from 'react';
function Account()
{
    var name=JSON.parse(localStorage.getItem('name'));
    var Email=JSON.parse(localStorage.getItem('Email'));
    const [pic,setpic]=useState("");
    const pichandler=(event=>{
        console.log(event.target.value)
        setpic(event.target.value)
    })
    return(
        <body>
        <header>
        <Navbar/>
    <main>
        <section class="left">
            <img src="https://tse4.mm.bing.net/th?id=OIP.2AWocslC3HKg_MXyzX6_JAHaEK&pid=Api&P=0&w=273&h=154" alt=""></img>
            <form>
                <b><label>Username</label></b><br></br>
                <p>{name}</p><br></br>
               <b><label>Email</label></b><br></br>
                <p>{Email}</p><br></br>
                <button style={{size:"2000px"}}>My orders
                </button><br></br>
                <button>Change password</button>
            </form>
        </section>
    </main>
    <input type='file' style={{borderRadius:"20px",backgroundColor:"purple"}} onChange={pichandler}></input> 
    </header>
    </body>
    )
}

export default Account
