import './x.css'
import Navbar from './Navbar'
import { useState } from 'react';
import { Axios } from 'axios';
function Account()
{
    var name=JSON.parse(localStorage.getItem('name'));
    var Email=JSON.parse(localStorage.getItem('Email'));
    const [pic,setpic]=useState("");
    const [flag,setflag]=useState(false);
    const pichandler=(event=>{
        setpic(event.target.value)
        console.log(pic.slice())
    })
    const submitHandler=()=>{

    }
    const changehandler=()=>{
        flag=true
        console.log("hello")
        Axios.post('',{
            body:{
               name:name,
            }
        })
    }

    return(
        <body>
        <header>
        <Navbar/>
    <main>
        <section class="left">
            <img src={pic}  name="avatar" accept=".png, .jpg, .jpeg" alt=""></img>
            <form onSubmit={pichandler}>
                <b><label>Username</label></b><br></br>
                <p>{name}</p><br></br>
               <b><label>Email</label></b><br></br>
                <p>{Email}</p><br></br>
                <button style={{size:"2000px"}}>My orders
                </button><br></br>
                <form> 
                <button type='submit' onSubmit={changehandler}>Change password</button>
                {flag==true?<input type="password"></input>:""}
                </form>
            </form>
        </section>
    </main>
    <input type='file' style={{borderRadius:"20px",backgroundColor:"purple"}} onChange={pichandler}></input> 
    <button type="submit">Submit</button>
    </header>
    </body>
    )
}

export default Account
