 import Navbar from './Navbar'
 import Card from './Card'
import Axios from 'axios';
import { useState } from 'react'
import { useEffect } from 'react';
import './Card.css'
 function Rooms(){

const [state, setstate] = useState("");
const [type,settype]=useState("");
const [Price, setPrice] = useState("");
const [Rooms,setRoom]=useState([
  {
    type:"Economic",
    Price:"250",
    source:"https://i.pinimg.com/originals/a6/dc/46/a6dc46afdf98a8125a71fc031d7673c3.png"
  }
])
useEffect(() => {
  console.log("start")
  Getdata()
}, [])
const [boolean,setBooleam]=useState(false);

const Getdata = () => {
Axios.get(`http://localhost:3001/Rooms`).then((result) => {
  setRoom(result)
  setBooleam(true)
}).catch(()=>{
});
};
const inputHandler=(event)=>{
setPrice(event.target.value)
setstate(event.target.value)
console.log(type)
Rooms.data.filter((object)=>{
  return(object.Price<=Price)
})
}
const typeHandler=(event)=>{
  settype(event.target.value)
  Rooms.data.filter((object)=>{
    return(
      object.type===type
      )
  })
}
    return(
<>
<Navbar/>

<h2 style={{marginTop:"20px",marginLeft:"500px"}}>Search Rooms</h2>
<b><hr size="5" width="100px" style={{marginTop:"20px",marginLeft:"550px",color:"#654321"}}></hr></b>
<div class="row">
  <div class="col" style={{marginLeft:"300px"}}>
  <label class="form-check-label">
  <label for="sel1">Room types:</label>
  <select class="form-control" id="sel1" onChange={typeHandler}>
      <option > </option>
    <option>Economic</option>
    <option >Buisness</option>
    <option >premium</option>
  </select>
  </label>
  </div>
  <div class="col">
  <p>Price:${state}</p>
  <input type="range" class="form-range" min="0" max="600"  onChange={inputHandler} style={{width:"100px",marginTop:"-10px",marginleft:"600px"}}></input>
</div>
</div>
<div className="fluid-container">
{
  
  boolean===true?Rooms.data.map((object)=>{
  return(
    <Card type={object.type} Price={object.Price} source={object.imgsrc}/>
  )
}):<p></p>
}
</div>
</>)
 }

export default Rooms;
