import './x.css'
import Navbar from './Navbar'
import { useState,useEffect} from 'react';
import Axios  from 'axios';
import Modal from 'react-modal'
function Account()
{
    const [pic,setpic]=useState("");
    const [password,setpassword]=useState("");
    const [modalISOpen,setModalISOpen]=useState(false)
    const [flag,setflag]=useState(false)
    useEffect(() => {
      var imgSource=JSON.stringify(localStorage.getItem(image))
        setpic("https://tse4.mm.bing.net/th?id=OIP.TUZxSRMq6m2pqs9UId34LAHaEo&pid=Api&P=0&w=255&h=160")

        // eslint-disable-next-line react-hooks/exhaustive-deps
    },[])
    const changehandler=(event)=>{
        // console.log(password)
        // event.preventDefault();
        // Axios.post('http://localhost:3000/update',{
        //        Name:name,
        //        Password:password
        // }).then((response)=>{
        //     alert("Updated successfully");
        // }).catch((error)=>{
        //     console.log("Error")
        // })
    }
    const passhandler=(event)=>{
        event.preventDefault()
        setpassword(event.target.value)
    }
    const [Rooms,setRoom]=useState([
        {
          username:"",
          Roomname:"",
          source:"",
        }])


    return(
        <>
        <Navbar/>
            <form class= "Block">
	        <img src={pic} alt="Avatar"></img>
			<br></br>
            <div className='lab' style={{marginTop:'30px'}}>
			<b><label>Username</label></b>
			<br></br>
            {/* <label>{name}</label>	 */}
			<br></br>
			<b><label>Email</label></b>
			<br></br>
            </div>
            {/* <label>{Email}</label> */}
			<br></br><br></br><br></br>
			<button  class="btn-dark" onClick={(event)=>{
                event.preventDefault()
            return(
            setModalISOpen(true))
            }}>My Orders</button>
            <button className="btn-dark" onClick={changehandler}>Change password</button>
            <input className='input' type="password" onChange={passhandler} ></input>
			<br></br>
	       </form>
    <Modal class="modal" isOpen={modalISOpen} style={{
    overlay: {
      position: 'fixed',
      left: '260px',
      backgroundColor: 'transparent',
      width:"700px",
      outline:"no",
      height:'500px'
    },
    content: {
      position: 'absolute',

    }
  }}>
<ul class="list-group">
  {flag===true?Rooms.data.map((object)=>{
        return(
  <li class="list-group-item " style={{display:'flex',justifyContent:'space-between'}}>
  <img className='image2' src={object.imgsrc} alt=""></img>
  <p>{object.Roomname}</p>
  </li>
        )
    }):<p></p>
  }
</ul>
  <button className='closing' style={{marginTop:'20px'}} onClick={()=>setModalISOpen(false)}>Close</button>
  </Modal>
    </>
    )
}

export default Account
