import './x.css'
import Navbar from './Navbar'
import { useState,useEffect,useContext} from 'react';
import Axios  from 'axios';
import {Token,Name,Email} from './Login'
function Account()
{
    const token=useContext(Token)
    const Username=useContext(Name)
    const Email=useContext(Email)

    const [pic,setpic]=useState("");
    const [password,setpassword]=useState("");
    useEffect(() => {
        setpic("https://tse4.mm.bing.net/th?id=OIP.TUZxSRMq6m2pqs9UId34LAHaEo&pid=Api&P=0&w=255&h=160")
    }, [])
    const changehandler=(event)=>{
        event.preventDefault();
        console.log(password)
        Axios.post('http://localhost:3001/update',{
               Name:Username,
               Password:password
        }).then((response)=>{
            console.log("Updates successfully")
        }).catch((error)=>{
            console.log("Error")
        })
    }
    const passhandler=(event)=>{
        setpassword(event.target.value)
    }
    const Imagehandler=(event)=>{
        console.log(event.target.value)
    }
    return(
        <body>
        <header>
        <Navbar/>
    <main>
        <section className="left">
            <img src={pic}  name="avatar" accept=".png, .jpg, .jpeg" alt=""></img>
            <form>

                <b><label>Username</label></b><br></br>
                <p>{Username}</p><br></br>
               <b><label>Email</label></b><br></br>
                <p>{Email}</p><br></br>
                <button style={{size:"2000px"}} onSubmit={passhandler}>Booked rooms
                </button><br></br>
                <button type='submit' onClick={changehandler}>Change password</button>
               <input type="password" style={{width:"100px"}} onChange={passhandler}></input> 
            </form>
        </section>
    </main>
    <input type='file' style={{borderRadius:"20px",backgroundColor:"purple"}} onChange={Imagehandler}></input> 
    <button type="submit" onClick={Imagehandler}>Submit</button>
    </header>
    </body>
    )
}

export default Account
