import React,{useState} from 'react'
import './home.css'
import Modal from 'react-modal'
import Axios from 'axios'
function Card(props)
{
  const [Roomname,setRoomname]=useState("")
  const [Username,setUsername]=useState("")
  const [Imgsrc,setImgsrc]=useState("")
  const [Description,setDesc]=useState("")

  const SubmitBooking=()=>{
    if(props.Name!=="")
    {
      console.log("hello")
    setUsername(props.Name)
    setRoomname(props.type)
    setImgsrc(props.source)
    setDesc(props.Desc)
    Axios.post('http://localhost:3001/Booking',
    {
      Room:Roomname,
      User:Username,
      image:Imgsrc,
      Description:Description
    })
  }
}
  const [modalISOpen,setModalISOpen]=useState(false)
    return(
      <div className="row1">
  <div class="card">
  <div class="card-title" style={{color:"black"}}>${props.Price}</div>
  <div class="card-body text-dark">
  <img class="img" src={props.source} alt=""></img>
  </div>
  <p class="para">{props.type}</p>
  <div className='Container'>
  <button onClick={()=>setModalISOpen(true)}>Description</button>
  </div>
  <Modal class="modal" isOpen={modalISOpen} style={{
    overlay: {
      position: 'fixed',
      left: '260px',
      backgroundColor: 'transparent',
      width:"700px",
      outline:"no"
    },
    content: {
      position: 'absolute',

    }
  }}>
  <h2>{props.type}</h2>
  <img className="image2" src={props.source} alt=""></img>
  <b><p>Description</p></b>
  <p>one</p>
  <p>two</p>
  <p></p>
  <button className='Booking' onClick={SubmitBooking}>Book</button>
  <button className='closing' onClick={()=>setModalISOpen(false)}>Close</button>
  </Modal>
</div>
</div>
    )
}

export default Card
