import "./x.css";
import Navbar from "./Navbar";
import { useState,useEffect} from "react";
import Modals from "./Components/model";
import { useSelector,useDispatch } from "react-redux";
import  {updateRoom} from './features/roomSlice/roomSlice'
import { createPayment } from "./utils/smartContract";
import Modal from "react-modal";
import axios from "axios";
function Account() {
  const [modalISOpen, setModalISOpen] = useState(false);
  const userId = useSelector((state) => state.Hotel.User.User.id);
  const Name = useSelector((state) => state.Hotel.User.User.Name);
  const Email = useSelector((state) => state.Hotel.User.User.Email);
  const img= useSelector((state) => state.Hotel.User.User.img);
  const [done,setDone]=useState(false)
const RoomsInfo = useSelector((state) => state.Hotel.Room.Room);
const [modalISOpen2, setModalISOpen2] = useState(false);
  const [bookedrooms,setbookedrooms]=useState([])
  const [final,setFinal]=useState([])
  let res=[]
   let bookedId=[]
     RoomsInfo.forEach(el => {
      const index = res.findIndex(obj => {
         return obj['id'] === el.id;
      });
      if(index === -1){
         res.push({
            "id": el.id,
            "type":el.type,
            "source":el.source,
            "price":el.price,
            "count": 1
         })
      }
      else{
         res[index]["count"]++;
      };
   });
   RoomsInfo.forEach(el => {
    const index = bookedId.findIndex(obj => {
       return obj['id'] === el.id;
    });
    if(index === -1){
      bookedId.push({
          "id": el.id,
       })
    }
    else{
      bookedId[index]["count"]++;
    };
 });
  let TotalPrice=0
  res.map((room)=>{
    TotalPrice+=room.price
  })
  const dispatch=useDispatch()
  async function fetchData() {
    let array = [];
    console.log("fetching...");
    try {
      const response = await axios.get("http://localhost:5004/api/Rooms/getuserRooms?userid=" + userId);
      console.log("resp1"+JSON.stringify(response))
      for (let i = 0; i < response.data.length; i++) {
        console.log(response.data[i].room_id)
        const response2 = await axios.get("http://localhost:5004/api/Rooms/getbyID?roomid=" + response.data[i].room_id+"&type="+'hello');
        console.log('response2'+response2.data);
        setFinal([...final,response2])
      }
      if(array.length>0){
        console.log('hello')
        setFinal(array)
      }
    } catch {
      setDone(false);
    }
  }
  useEffect(async()=>{
    if(final.length>0){
      setbookedrooms(final)
      setDone(true);
    }
   },[final])
  useEffect(async()=>{
   await fetchData()
  },[])
  useEffect(()=>{
    console.log("hello2")
    console.log(final)
console.log(bookedrooms)
  },[done])
  useEffect(() => {

  }, [RoomsInfo])
  const headingStyle = {
    backgroundImage: "linear-gradient(to right, gray, red)",
    WebkitBackgroundClip: "text",
    backgroundClip: "text",
    WebkitTextFillColor: "transparent",
    textFillColor: "transparent",
    fontWeight: "extrabold",
  };
const DeleteRoomHandler=(id)=>{
dispatch(updateRoom(RoomsInfo.filter((object)=>{
  return object.id!==id
})))
}
const insertOnwers=async(event)=>{
  event.preventDefault()
  const flattenedArray = bookedId.reduce((result, obj) => {
    return [...result, obj.id];
  }, []);
  await createPayment(userId,TotalPrice,flattenedArray)
  alert("Payment Request done")
}
  return (
    <>
      <Navbar />
      <form class="Block">
         <h3 style={headingStyle}>Your Information</h3>
         <br></br>
         <img
          style={{ height: "300px", width: "300px",objectFit:'cover' }}
          src={img}
          alt="Avatar"
       />
        <br></br>
        <div className="lab" style={{ marginTop: "30px" }}>

          <b>
            <label>Username</label>
          </b>
          <br></br>
          <label>{Name}</label>
          <br></br>
          <b>
            <label>Email</label>
          </b>
          <br></br>
        </div>
        <label>{Email}</label>
        <br></br>
        <button
          class="btn-dark"
          onClick={(event) => {
            event.preventDefault();
            setModalISOpen(true);
          }}
        >
          My Orders
        </button>
        {
          <button
          class="btn-dark"
          onClick={(event) => {
            event.preventDefault();
           setModalISOpen2(true);
          }}
        >
       Connect Wallet
        </button>
        }
       
      </form>
      <Modals modalISOpen={modalISOpen} setModalISOpen={setModalISOpen} res={res} DeleteRoomHandler={DeleteRoomHandler} insertOnwers={insertOnwers} sum={TotalPrice}/>
    </>
  );
}

export default Account;
