import React,{useState} from 'react'
import './home.css'
import Modal from 'react-modal'
import Axios from 'axios'
function Card(props)
{
  const [Roomname,setRoomname]=useState("")
  const [Username,setUsername]=useState("")
  const [Imgsrc,setImgsrc]=useState("")
  const [Description,setDesc]=useState("")

  const SubmitBooking=()=>{
    if(props.Name!=="")
    {
    setUsername(props.Name)
    setRoomname(props.type)
    setImgsrc(props.source)
    setDesc(props.Desc)
    console.log(Roomname)
    console.log(Username)
    console.log(Imgsrc)
    Axios.post('http://localhost:3001/Booking',
    {
      Room:Roomname,
      User:Username,
      image:Imgsrc,
      Description:Description
    }).then((result)=>{
      alert("data inserted")
    })
  }
}
  const [modalISOpen,setModalISOpen]=useState(false)
    return(
  <>
    <div className="card" style={{backgroundImage:`url(${props.source})`}}>
       <div className='card-Price'>
       <p>${props.Price}</p>
       </div>
        <div className="card-content">
        <h2 className="card-title" >{props.type}</h2>
          <p className="card-body">{props.price}</p>
          <button className="buttons" onClick={()=>setModalISOpen(true)}>Description</button>
        </div>
      </div>
  <Modal class="modal" isOpen={modalISOpen} style={{
    overlay: {
      position: 'fixed',
      left: '260px',
      backgroundColor: 'transparent',
      width:"700px",
      outline:"no"
    },
    content: {
      position: 'absolute',

    }
  }}>
  <h2>{props.type}</h2>
  <img className="image2" src={props.source} alt=""></img>
  <b><p>Description</p></b>
  <p>{props.Description}</p>
  <button className='Booking' onClick={SubmitBooking}>Book</button>
  <button className='closing' onClick={()=>setModalISOpen(false)}>Close</button>
  </Modal>
</>
    )
}

export default Card
