import './x.css'
import Navbar from './Navbar'
import { useState,useEffect} from 'react';
import Axios  from 'axios';
import Modal from 'react-modal'
function Account()
{
    useEffect(() => {
        GetbookedRoom()
      // eslint-disable-next-line react-hooks/exhaustive-deps
      },[])
    var name=JSON.parse(localStorage.getItem('name'));
    var Email=JSON.parse(localStorage.getItem('Email'));
    const [pic,setpic]=useState("");
    const [password,setpassword]=useState("");
    const [modalISOpen,setModalISOpen]=useState(false)
    useEffect(() => {
        setpic("https://tse4.mm.bing.net/th?id=OIP.TUZxSRMq6m2pqs9UId34LAHaEo&pid=Api&P=0&w=255&h=160")
    }, [])
    const changehandler=(event)=>{
        console.log(password)
        event.preventDefault();
        Axios.post('http://localhost:3001/update',{
               Name:name,
               Password:password
        }).then((response)=>{
            console.log("Updates successfully")
            alert("Updates successfully")
        }).catch((error)=>{
            console.log("Error")
        })
    }
    const passhandler=(event)=>{
        event.preventDefault()
        setpassword(event.target.value)
    }
    const [Rooms,setRoom]=useState([
        {
          username:"",
          Roomname:"",
          source:"",
        }])
const GetbookedRoom= (event) => {
    console.log("hello")
  Axios.get(`http://localhost:3001/BookedRooms?name=${name}`).then((result) => {
      alert("data successful")
      console.log(result)
      setRoom(result)
  }).catch(()=>{
    alert("Invalid information")
  });
};
    return(
        <>
        <Navbar/>
            <form class= "Block">
	        <img src={pic} alt="Avatar"></img>
			<br></br>
            <div className='lab' style={{marginTop:'30px'}}>
			<b><label>Username</label></b>
			<br></br>
            <label>{name}</label>	
			<br></br>
			<b><label>Email</label></b>
			<br></br>
            </div>
            <label>{Email}</label>
			<br></br><br></br><br></br>
			<button  class="btn-dark" onClick={(event)=>{
                event.preventDefault()
            return(
            setModalISOpen(true))
            }}>My Orders</button>
            <button className="btn-dark" onClick={changehandler}>Change password</button>
            <input className='input' type="password" onChange={passhandler} ></input>
			<br></br>
	       </form>
    <Modal class="modal" isOpen={modalISOpen} style={{
    overlay: {
      position: 'fixed',
      left: '260px',
      backgroundColor: 'transparent',
      width:"700px",
      outline:"no",
      height:'500px'
    },
    content: {
      position: 'absolute',

    }
  }}>
<ul class="list-group">
  {
    Rooms.map((object)=>{
        return(
  <li class="list-group-item " style={{display:'flex',justifyContent:'space-between'}}>
  <img src={object.imgsrc} alt=""></img>
  <p>{object.Roomname}</p>
  <button style={{height:"50px"}}> Checkout</button>
  </li>
        )
    })
  }
</ul>
  <button className='closing' style={{marginTop:'20px'}} onClick={()=>setModalISOpen(false)}>Close</button>
  </Modal>
    </>
    )
}

export default Account
