import './x.css'
import Navbar from './Navbar'
import { useState,useEffect} from 'react';
import Axios  from 'axios';
import Modal from 'react-modal'
function Account()
{
    var name=JSON.parse(localStorage.getItem('name'));
    var Email=JSON.parse(localStorage.getItem('Email'));
    const [pic,setpic]=useState("");
    const [password,setpassword]=useState("");
    const [modalISOpen,setModalISOpen]=useState(false)
    useEffect(() => {
        setpic("https://tse4.mm.bing.net/th?id=OIP.TUZxSRMq6m2pqs9UId34LAHaEo&pid=Api&P=0&w=255&h=160")
    }, [])
    const changehandler=(event)=>{
        event.preventDefault();
        console.log(password)
        Axios.post('http://localhost:3001/update',{
               Name:name,
               Password:password
        }).then((response)=>{
            console.log("Updates successfully")
        }).catch((error)=>{
            console.log("Error")
        })
    }
    const passhandler=(event)=>{
        setpassword(event.target.value)
    }
    const Imagehandler=(event)=>{
        console.log(event.target.value)
    }
    return(
        <>
        <Navbar/>
            <form class= "Block">
	        <img src={pic} alt="Avatar"></img>
			<br></br>
			<b><label>Username</label></b>
			<br></br>
            <label>{name}</label>	
			<br></br>
			<b><label>Email</label></b>
			<br></br>
            <label>{Email}</label>
			<br></br><br></br><br></br>
			<button  class="btn-dark" onClick={(event)=>{
                event.preventDefault()
            return(
            setModalISOpen(true))
            }}>My Orders</button>
            <button  class="btn-dark">Change password</button>
            <input className='input' onChange={passhandler} ></input>
			<br></br>
	       </form>
    <Modal class="modal" isOpen={modalISOpen} style={{
    overlay: {
      position: 'fixed',
      left: '260px',
      backgroundColor: 'transparent',
      width:"700px",
      outline:"no"
    },
    content: {
      position: 'absolute',

    }
  }}>
<ul class="list-group">
  <li class="list-group-item ">Active item</li>
  <li class="list-group-item">Second item</li>
  <li class="list-group-item">Third item</li>
</ul>
  <button className='closing' onClick={()=>setModalISOpen(false)}>Close</button>
  </Modal>
    </>
    )
}

export default Account
