import React,{useState} from 'react'
import './Card.css'
import Modal from 'react-modal'
function Card(props)
{
  const [modalISOpen,setModalISOpen]=useState(false)
    return(
      <div className="row1">
  <div class="card" style={{"max-width":"18rem"}}>
  <div class="card-header" style={{color:"black"}}>${props.Price}</div>
  <div class="card-body text-dark">
  <img class="img" src={props.source} alt=""></img>
  </div>
  <p class="para">{props.type}</p>
  <button onClick={()=>setModalISOpen(true)}>Description</button>
  <Modal class="modal" isOpen={modalISOpen} style={{
    overlay: {
      position: 'fixed',
      top: 0,
      left: '240px',
      right:0,
      bottom: 0,
      backgroundColor: 'transparent',
      width:"600px",
      outline:"no"
    },
    content: {
      position: 'absolute',

    }
  }}>
  <h2>Modal title</h2>
  <p>Modal body</p>
  <button onClick={()=>setModalISOpen(false)}>Close</button>
  </Modal>
</div>
</div>
    )
}

export default Card
