import './Card.css'
function Card(props)
{
    return(
      <div>
  <div class="card" style={{"max-width":"18rem"}}>
  <div class="card-header" style={{color:"black"}}>${props.Price}</div>
  <div class="card-body text-dark">
  <img class="img" src={props.source} alt=""></img>
  </div>
  <p class="para">{props.type}</p>
</div>
</div>
    )
}

export default Card
